# Which tx in block 216,351 spends the coinbase output of block 216,128?
bitcoin-cli -conf=$HOME/.bitcoin/bitcoin.conf getblock $(bitcoin-cli -conf=$HOME/.bitcoin/bitcoin.conf getblockhash 216351) 2 | jq -r '.tx[] | select(.vin[].txid == "'$(bitcoin-cli -conf=$HOME/.bitcoin/bitcoin.conf getblock $(bitcoin-cli -conf=$HOME/.bitcoin/bitcoin.conf getblockhash 216128) 2 | jq -r '.tx[0].txid')'") | .txid'
